parseSearchQuery(query) {
const terms = [];
const parts = query.split(' ').filter(part => part.trim());
for (const part of parts) {
if (part.includes('|')) {
const orKeywords = part.split('|').map(k => k.trim()).filter(k => k);
if (orKeywords.length > 0) {
terms.push({
type: 'or',
keywords: orKeywords
});
}
} else {
terms.push({
type: 'and',
keywords: [part.trim()]
});
}
}
return terms;
}
